import { Injectable } from '@angular/core';
import { ActivatedRouteSnapshot, Resolve } from '@angular/router';
import { Observable, zip } from 'rxjs';
import { map } from 'rxjs/operators';
import { IProductItem } from '../models/product.model';
import { ProductService } from '../services/product.service';
import {
  CategoryService,
  IBrandBackend,
  ICategoryBackend,
} from '../services/category.service';

@Injectable({
  providedIn: 'root',
})
export class BrandResolver implements Resolve<IBrandPage> {
  constructor(private catService: CategoryService) {}
  resolve(route: ActivatedRouteSnapshot): Observable<IBrandPage> {
    const friendlyUrl = route.paramMap.get('friendlyUrl');
    const brandFriendlyUrl = route.paramMap.get('friendlyUrl');
    return zip(this.catService.getCategoryElement(friendlyUrl)).pipe(
      map(([categoryElement]) => {
        const catPage: IBrandPage = {
          category: categoryElement.entity,
          _brands: categoryElement.brands.data,
          isBrandPage: true,
        };
        this.catService.urlAttachment = categoryElement.url_attachment;
        return catPage;
      })
    );
  }
}

export interface IBrandPage {
  category: ICategoryBackend;
  _brands: IBrandBackend[];
  isBrandPage: boolean;
}

