@Injectable({
  providedIn: 'root',
})
export class ProductService {
  constructor(private api: ApiService) {}

  getFeaturedProducts(): Observable<IProductItem[]> {
    // return this.api.get('category-group/list');
    return of(products);
  }

  getProducts(filters: any, categoryId: string): Observable<IProductBackend[]> {
    const params = this.api.createHttpParams({
      data: { filters, category_id: categoryId },
    });
    console.log('query filters', filters);
    return this.api.get(`product/category-filter`, params);
  }
}

