import { Component, EventEmitter, Input, OnInit, Output, ViewChild } from '@angular/core';
import { MDBModalRef, MDBModalService } from 'angular-bootstrap-md';
import { INavBar } from 'src/app/models/navbar.model';
import { NavBarComponent } from './nav-bar/nav-bar.component';
import { ProfileComponent } from 'src/app/modals/profile/profile.component';
import { LoginComponent } from 'src/app/modals/login/login.component';
import { WishlistComponent } from 'src/app/modals/wishlist/wishlist.component';
import { CartComponent } from 'src/app/modals/cart/cart.component';
import { NavBarMobileComponent } from './nav-bar-mobile/nav-bar-mobile.component';
import { Router } from '@angular/router';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { navBar } from 'src/app/mockups/header.mockup';
import { CategoryService, ICategoryListBackend } from 'src/app/services/category.service'

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.scss']
})
export class HeaderComponent implements OnInit {

  modalRef: MDBModalRef;
  @Input() user: boolean;
  @Input() navBarListConfig: INavBar = navBar;

  @ViewChild('navBar', { static: true }) navBar: NavBarComponent;
  @ViewChild('navBarMobile', { static: true }) navBarMobile: NavBarMobileComponent;
  searchForm: FormGroup;

  constructor(private modalService: MDBModalService, private router: Router, private categoryService: CategoryService) { }

  ngOnInit(): void {
    this.user = localStorage.getItem('jwt')? true:false;

    this.searchForm = new FormGroup({
      filter: new FormControl('', [Validators.required]),
    });

    this.categoryService.getCategoryList().subscribe( (result: ICategoryListBackend) => {
      this.navBarListConfig = {
        navBarList: []
      }
      result.data.forEach( (category) => {
        this.navBarListConfig.navBarList.push({
          name: category.name,
          categoryList: [],
          selected: false,
          friendlyUrl: category.friendly_url,
        })
        category.filters[0].values.forEach( (filter) => {
          this.navBarListConfig.navBarList[this.navBarListConfig.navBarList.length - 1].categoryList.push(
            {
              name: filter,
              filter: category.filters[0].filter
            }
          )
        })
      })
    })

  }

  setFilter(e): void {
    if (e.keyCode == 13) {
      this.router.navigate(['/main/search'], { queryParams: { filter: this.searchForm.controls['filter'].value } });
    }
  }

  findByFilter(): void {
    this.router.navigate(['/main/search'], { queryParams: { filter: this.searchForm.controls['filter'].value } });
  }

  showCategories() {
    this.navBar.toggleNavBar();
  }

  showCategoriesMobile() {
    this.navBarMobile.toggleNavBar();
  }

  openLogin() {
    this.modalRef = this.modalService.show(LoginComponent, {
      backdrop: true,
      keyboard: true,
      focus: true,
      show: false,
      ignoreBackdropClick: false,
      class: "modal-full-height modal-right mh-100 my-0",
      containerClass: "right modal-dialog-scrollable mh-100 my-0",
      animated: true,
    })
  }

  openAccountData() {
    this.modalRef = this.modalService.show(ProfileComponent, {
      backdrop: true,
      keyboard: true,
      focus: true,
      show: false,
      ignoreBackdropClick: false,
      class: "modal-full-height modal-right mh-100 my-0",
      containerClass: "right modal-dialog-scrollable mh-100 my-0",
      animated: true,
    })
  }

  openWishlist() {
    this.modalRef = this.modalService.show(WishlistComponent, {
      backdrop: true,
      keyboard: true,
      focus: true,
      show: false,
      ignoreBackdropClick: false,
      class: "modal-full-height modal-right mh-100 my-0",
      containerClass: "right modal-dialog-scrollable mh-100 my-0",
      animated: true,
    })
  }

  openCart() {
    this.modalRef = this.modalService.show(CartComponent, {
      backdrop: true,
      keyboard: true,
      focus: true,
      show: false,
      ignoreBackdropClick: false,
      class: "modal-full-height modal-right mh-100 my-0",
      containerClass: "right modal-dialog-scrollable mh-100 my-0",
      animated: true,
    })
  }
}

styleUrlsData.data:
@use 'variables';

:host {
  header {
    position: fixed;
    right: 0;
    left: 0;
    z-index: variables.$z-header;
    border-width: 0 0 1px;
    margin-bottom: 20px;
  }
  .logo {
    @media (max-width: 990px) {
      width: 150px;
      @media (max-width: 355px) {
        width: 110px;
      }
    }
  }
  .category-group {
    width: 100%;
    .nav-item {
      .nav-link {
        padding-left: 2rem;
        padding-right: 2rem;
        @media (max-width: 990px) {
          padding-left: 0rem;
          padding-right: 0rem;
        }
      }
    }
  }

  .nav-item {
    position: relative;
    a.active,
    a:not(.icon):hover {
      transition: all 0.4s ease-in-out;
      font-weight: bold;
    }
    a.active::before,
    a:not(.icon)::before {
      content: '';
      transition: all 0.4s ease-in-out;
      position: absolute;
      right: 50%;
      left: 50%;
      bottom: 0;
      height: 2px;
      width: 0px;
      background-color: red;
    }
    a.active::before,
    a:not(.icon):hover::before {
      right: 0;
      left: 0;
      width: 100%;
    }
  }
  .search-box{
    width: 50%;
    @media (max-width: 990px) {
      width: 100%;
    }
    input{
      height: calc(1.5em + 0.75rem);
      border-radius: 0.25rem 0rem 0rem 0.25rem;
      width: calc(100% - 80px) !important;
      @media (max-width: 990px) {
        width: calc(100% - 47px) !important;
      }
    }
    button {
      border-radius: 0rem 0.25rem 0.25rem 0rem;
    }
  }
  .bottom-shadow{
    box-shadow: 0px 10px 10px rgba(0,0,0,0.2);
  }

  .invisible-scrollable{
    overflow-x: auto;
    white-space: nowrap;
    -webkit-overflow-scrolling: touch;
    -ms-overflow-style: -ms-autohiding-scrollbar;
  }
  .invisible-scrollable::-webkit-scrollbar {
    display: none;
  }
}

