import { Component, OnInit, Input } from '@angular/core';
import { IProductItem, ProductItemType } from 'src/app/models/product.model';
import {
  CategoryService,
  IBrandBackend,
  IFilterBackend,
} from 'src/app/services/category.service';
import {
  IProductBackend,
  ProductService,
} from 'src/app/services/product.service';

@Component({
  selector: 'app-product-list',
  templateUrl: './product-list.component.html',
  styleUrls: ['./product-list.component.scss'],
})
export class ProductListComponent implements OnInit {
  @Input() products: IProductBackend[] = [];
  @Input() filters: IFilterBackend[] = [];
  @Input() _brands: IBrandBackend[] = [];
  @Input() type: ProductItemType = 'scotia';
  @Input() isPaginated = false;
  @Input() itemsPerPage = 10;
  @Input() loading = true;
  currentPage = 1;

  constructor(
    private productService: ProductService,
    private catService: CategoryService
  ) {}

  ngOnInit(): void {}

  pageChanged(index) {
    this.currentPage = index;
  }

  menuChanged(menuFilters) {
    this.loading = true;
    this.productService
      .getProducts(menuFilters, this.catService.category._id)
      .subscribe((res) => {
        this.products = res;
        this.loading = false;
      });
  }
}

import { Component, EventEmitter, Input, OnInit, Output } from '@angular/core';
import { FormArray, FormBuilder, FormGroup } from '@angular/forms';
import {
  IBrandBackend,
  IFilterBackend,
} from 'src/app/services/category.service';
import { Constants } from 'src/app/constants';

@Component({
  selector: 'app-product-list-menu',
  templateUrl: './product-list-menu.component.html',
  styleUrls: ['./product-list-menu.component.scss'],
})
export class ProductListMenuComponent implements OnInit {
  @Input() filters: IFilterBackend[];
  @Input() _brands: IBrandBackend[];
  @Output() menuChange = new EventEmitter<any>();

  formMenu = this.fb.group({});

  // Getters for the html
  get formFilters() {
    return (this.formMenu.get('formFilters') as FormArray)?.controls;
  }
  get formBrands() {
    return ((this.formMenu.get('formBrand') as FormGroup)?.get(
      'value'
    ) as FormArray)?.controls;
  }

  constructor(private fb: FormBuilder) {}

  ngOnInit(): void {
    this.initFormFilters();
    this.formMenu.valueChanges.subscribe((val) => {
      this.sendFormFilters(val);
    });
    console.log(this.formMenu.value);
    this.sendFormFilters(this.formMenu.value);
  }

  initFormFilters() {
    // Add every form group|array to formMenu
    // price, points, brands, filters
    const formPrice = this.fb.group(
      {
        bindedTo: ['precio'],
        used: [true],
        value: this.fb.array([this.fb.control(null), this.fb.control(null)]),
      },
      {
        validators: (controls) => {
          return (controls.get('value') as FormArray).controls[0].value <
            (controls.get('value') as FormArray).controls[1].value
            ? null
            : {
                errorPricesInvalid: true,
              };
        },
      }
    );
    this.formMenu.addControl('formPrice', formPrice);

    const formPoints = this.fb.group(
      {
        bindedTo: ['precio'],
        used: [true],
        value: this.fb.array([this.fb.control(null), this.fb.control(null)]),
      },
      {
        validators: (controls) => {
          return (controls.get('value') as FormArray).controls[0].value <
            (controls.get('value') as FormArray).controls[1].value
            ? null
            : {
                errorPricesInvalid: true,
              };
        },
      }
    );
    this.formMenu.addControl('formPoints', formPoints);

    const formBrand = this.fb.group({
      bindedTo: ['marca'],
      used: [true],
      value: this.fb.array(
        this._brands.map((val) =>
          this.fb.group({
            name: [val.name],
            toggle: [true],
          })
        )
      ),
    });
    this.formMenu.addControl('formBrand', formBrand);

    const formFilters = this.fb.array(
      this.filters.map((filter) =>
        this.fb.group({
          bindedTo: [''],
          name: [(filter as any).filter.name],
          used: [true],
          filter_id: [filter._id],
          type: ['checkbox'],
          value: this.fb.array(
            filter.values.map((val) =>
              this.fb.group({
                name: [val],
                toggle: [true],
              })
            )
          ),
        })
      )
    );
    this.formMenu.addControl('formFilters', formFilters);
  }

  sendFormFilters(val = {}) {
    // Mildly validate the values of formMenu
    // so the back receives|processes less params
    let flatFormArr = [];
    if (this.formMenu.get('formPrice').valid && val['formPrice'])
      flatFormArr.push(val['formPrice']);
    if (this.formMenu.get('formPoints').valid && val['formPoints']) {
      // shallow copy to avoid modifying source obj
      let formPointsCopy = Object.assign({}, val['formPoints']);
      // points is the price divided by POINTS_DIVIDER
      formPointsCopy.value = (formPointsCopy.value as [
        number,
        number
      ])?.map((val) => Number((val / Constants.POINTS_DIVIDER).toFixed(2)));

      // merge with price filter if price is also valid
      if (this.formMenu.get('formPrice').valid && val['formPrice']) {
        flatFormArr = [];
        // intersect the filter amount values
        if (val['formPrice'].value[0] > formPointsCopy.value[0])
          formPointsCopy.value[0] = val['formPrice'].value[0];
        if (val['formPrice'].value[1] < formPointsCopy.value[1])
          formPointsCopy.value[1] = val['formPrice'].value[1];
      }
      flatFormArr.push(formPointsCopy);
    }

    if (this.formMenu.get('formBrand').dirty && val['formBrand'])
      flatFormArr.push(val['formBrand']);
    if (this.formMenu.get('formFilters').dirty && val['formFilters'])
      flatFormArr.push(...val['formFilters']);

    // then emit them
    this.menuChange.emit(flatFormArr);
  }
}

